const chai = require('chai');
const expect = require('chai').expect;
const should = chai.should();

const downloadHtml = require('../dist/index').downloadHtml;

describe('download-html', () => {
  it('downloads html from url', (done) => {
    downloadHtml('http://google.pt')
      .then((html) => {
        done();
        expect(html).to.be.an('object');
        expect(html).to.have.property('data');
      });
  });
});